// RemoveAsset relationship.
// Sets o.R.Asset to nil.
// Removes o from all passed in related items' relationships struct (Optional).
func (o *Device) RemoveAsset(ctx context.Context, exec boil.ContextExecutor, related *Asset) error {
	var err error

	queries.SetScanner(&o.AssetID, nil)
	if _, err = o.Update(ctx, exec, boil.Whitelist("asset_id")); err != nil {
		return errors.Wrap(err, "failed to update local table")
	}

	o.R.Asset = nil
	if related == nil || related.R == nil {
		return nil
	}

	for i, ri := range related.R.AssetDevices {
		if queries.Equal(o.AssetID, ri.AssetID) {
			continue
		}

		ln := len(related.R.AssetDevices)
		if ln > 1 && i < ln-1 {
			related.R.AssetDevices[i] = related.R.AssetDevices[ln-1]
		}
		related.R.AssetDevices = related.R.AssetDevices[:ln-1]
		break
	}
	return nil
}

