// FindRisk retrieves a single record by ID with an executor.
// If selectCols is empty Find will return all columns.
func FindRisk(ctx context.Context, exec boil.ContextExecutor, riskID int, selectCols ...string) (*Risk, error) {
	riskObj := &Risk{}

	sel := "*"
	if len(selectCols) > 0 {
		sel = strings.Join(strmangle.IdentQuoteSlice(dialect.LQ, dialect.RQ, selectCols), ",")
	}
	query := fmt.Sprintf(
		"select %s from `Risk` where `risk_id`=?", sel,
	)

	q := queries.Raw(query, riskID)

	err := q.Bind(ctx, exec, riskObj)
	if err != nil {
		if errors.Cause(err) == sql.ErrNoRows {
			return nil, sql.ErrNoRows
		}
		return nil, errors.Wrap(err, "models: unable to select from Risk")
	}

	return riskObj, nil
}

